import { useRef, useState } from 'react';
import Input from '../../UI/Input';
import classes from './BookItemForm.module.css';



const BookItemForm = (props) => {
    const [isChecked, setisChecked] = useState(false);
    const bookRef = useRef();
    const submitHandler = event =>{
        event.preventDefault();
        console.log(bookRef.current.checked);
        const bookSelect = bookRef.current.value;
        props.onAddToCart(bookSelect);
    }
    return (
        <div>
            <form className={classes.form +" " + classes.conatiner} onSubmit={submitHandler} > 
                 <Input 
                    ref={bookRef}
                    input={{
                        id: "amount_" + props.id,
                        type:'number',
                        min:'1',
                        max: '5',
                        step: '1',
                        defaultValue: '1'
                        
                    }}
                />
                <button>
                    Add
                </button>
                
            </form>
        </div>
    )
}

export default BookItemForm;
