import { useRef, useState } from 'react';
import Input from '../../UI/Input';
import classes from './BookItemForm.module.css';



const BookItemForm = (props) => {
    const [isChecked, setisChecked] = useState(false);
    const bookRef = useRef();
    const submitHandler = event =>{
        event.preventDefault();
        console.log(bookRef.current.checked);
        const bookSelect = bookRef.current.value;
        props.onAddToCart(bookSelect);
    }
    return (
        <div>
            <form className={classes.form} onSubmit={submitHandler}>
                <Input 
                    ref={bookRef}
                    label="Add to Cart"
                    input={{
                        id: "chk_" + props.isbn,
                        type:'checkbox',
                        
                    }}
                />
                {isChecked && <div>Checked!</div>}
                <button>
                    Add
                </button>
            </form>
        </div>
    )
}

export default BookItemForm;
