import React, {useState} from 'react';
import Cart from './components/Cart/Cart';
import Header from "./components/Layout/Header";
import Meals from './components/Meals/Meals';
import CartProvider from './store/CartProvider';
function App() {

  const fetchFoodHandler = useCallback( async () => { 
    // setIsLoading(true);
    // setError(null);
    try {
     const response = await fetch('https://react-restaurant-app-70a9b-default-rtdb.firebaseio.com/meals'); 
     if(!response.ok) {
       throw new Error('Something went wrong!')
     }
     const data = await response.json();
     const loadedMeals = [];
     for(const key in data) {
       loadedMeals.push({
         id:key,
         name:data[key].name,
         description:data[key].description,
         price:data[key].price
       })
     }
     setMeals(loadedMeals);
    }
    catch (error) {
      setError(error.message);
    }
    // setIsLoading(false);
   },
    [],
  )

  const[cartIsShown, setCartIsShown] = useState(false);

  const showCardHandler = (event) => {
    setCartIsShown(true);
  };

  const hideCardHandler = () => {
    setCartIsShown(false);
  };


  return (
  <CartProvider>
      {cartIsShown && <Cart onHideCart={hideCardHandler}/>}
      <Header onShowCart={showCardHandler} />
      <main>
        <Meals />

      </main>
      </CartProvider>

  );
}

export default App;
