import {useContext} from 'react';
import Modal from '../UI/Modal';
import classes from './Cart.module.css';
import CartContext from '../../store/cart-context';
import CartItem from './CartItem';

const Cart = (props) => {
    const cartCtx = useContext(CartContext);
    const totalCount = cartCtx.totalAmount;
    const cartItemRemoveHandler = (id) => {
        cartCtx.removeItem(id);
    };
    const cartItemAddHandler = (item) => {
        cartCtx.addItem({...item, amount:1});
    }
    const cartItems = (
        <ul className={classes['cart-itesm']}>
            {
                cartCtx.items.map((item) => (
                    <CartItem 
                        isbn = {book.isbn}
                        title = {book.title}
                        subtitle = {book.subtitle}
                        author = {book.author}
                        published = {book.published}
                        publisher = {book.publisher}
                        pages = {book.pages}
                        description = {book.description}
                        website = {book.website}
                        inStock = {book.inStock}
                />

                ))
            }
        </ul>
    );
    return(
        <Modal onHideCartHandler={props.onHideCart}>
            {cartItems}
            <div className={classes.total}>
                <span>Number of Books</span>
                <span>{totalCount}</span>
            </div>
            <div className = {classes.actions}>
                <button className={classes["button--alt"]} onClick={props.HideCart}> Close</button>
                {hasItems && <button className={classes.button}>Check Out</button>}
            </div>
        </Modal>
    );
}

export default Cart;
