import {useState, useEffect, useCallback} from 'react';
import classes from './AvailableMeals.module.css';
import MealItem from './MealItem/MealItem';
import Card from '../UI/Card';

// const [meals,setMeals] = useState();
// const [isLoading,setIsLoading] = useState();
// const [error, setError] = useState();



  // setIsLoading(true);
  // setError(null);
  


const DUMMY_MEALS = [
  {
    id: 'm1',
    name: 'Sushi',
    description: 'Finest fish and veggies',
    price: 22.99,
  },
  {
    id: 'm2',
    name: 'Schnitzel',
    description: 'A german specialty!',
    price: 16.5,
  },
  {
    id: 'm3',
    name: 'Barbecue Burger',
    description: 'American, raw, meaty',
    price: 12.99,
  },
  {
    id: 'm4',
    name: 'Green Bowl',
    description: 'Healthy...and green...',
    price: 18.99,
  },
];

const AvailableMeals = (props) => {
    const [meals, setMeals] = useState([]);  
    const [isLoading, setIsLoading] = useState(false);
    const [error,setError] = useState();

 
 useEffect(() => {
  const fetchMeals = async () => {
    setIsLoading(true);
    const response = await fetch('https://react-restaurant-app-70a9b-default-rtdb.firebaseio.com/meals'); 
    console.log("Data has been logged");
    console.log(response);
    if(!response.ok) {
      throw new Error('Something went wrong!')
    }
    const data = await response.json();
    const loadedMeals = [];
    for(const key in data) {
      loadedMeals.push({
        id:key,
        name:data[key].name,
        description:data[key].description,
        price:data[key].price
      })
    }
    setMeals(loadedMeals);
    setIsLoading(false);
    console.log(loadedMeals);
  };
    fetchMeals().catch(error => {
      setIsLoading(false);
      setError(error.message);

    });

  }, []);
    const mealsList = meals.map(meal => (
    <MealItem 
        id={meal.key}
        key={meal.key}
        name={meal.name}
        description={meal.description} 
        price={meal.price} 
    />
    ));
      let content = '';
    console.log(isLoading);
    if(isLoading) {
      content = <p>Loading...</p>
    } 
    console.log(error);
    if(error != ''){
      return (
        content= <p className={classes.mealsError}>{error}</p>
      )
    }

    return (
      <section className={classes.meals}>
        <Card>
          <ul>
            <section>{mealsList}</section>
            {isLoading && <section>{content}</section>}
          </ul>
        </Card>
      </section>
    )
}

export default AvailableMeals;
